/************************************************************************
 * Project: PONG
 * 
 * TODO LIST:
 * 1. Create a canvas : Ball, Paddle, Score 
 * 2. Implement the game logic : status, position, score 
 * 3. Implement the game loop : events = mouse move
 * 4
 ************************************************************************ */


import React from 'react'; /* React is JS lib buildon UI interactive apps Web/Mobile*/
import ReactDOM from 'react-dom/client'; /* ReactDOM is for web */
import './index.css';


function numberFormat({number}) {
  return number < 10 ? `00:0${number}` : number;
}

// ============================================================================//

// ============================================================================//
class Timer extends  React.Component {
  constructor(props) {
    // call the parent constructor
    super(props)

    this.state = {timer : props.start}
    this.time = null
  }

  //  be called when the component is mounted
  componentDidMount() { 
    window.setInterval(this.tick.bind(this), 1000)
  }
  // called when the component is unmounted
  componentWillUnmount(){
    this.cleaInterval(this.time)
  }
  // called every second and increment the timer
  tick() {
    this.setState((state, props) => ({timer: state.timer + props.step}))
  }

  render() 
  {
	  return (
	  <div >Game : {this.state.timer} </div> 
	)}
}

class Game extends React.Component {

	  render() {
		return(
      <div className="game">
        <div className="game-board">
          <h1>Salut</h1>
         < Timer start={60} step={10} />
        </div>
      </div>    
    )
	  }
}  


// ============================================================================//

// create a root element
// React Dom is for web it will render the game component in the root element
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);

window.setInterval(() => {

    root.render(<Game />, document.getElementById("root"));
}, 1000);
