import React from 'react'; 

import {Ball} from './Ball.jsx';
import {Paddle} from './Paddles.jsx'

// rerennder the board component when the state changes
export class   Board extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            ballX: 0,
            ballY: 0,
            ballSpeedX: 1,
            ballSpeedY: 0,
            Paddle1Height: 0,
            Paddle2Height: 0,
            player1Score: 0,
            player2Score: 0,
            time: 0,
            MousePosition: { x: 0, y: 0 },
        };
    }

    handleKeyPress = (event) => {
        switch (event.keyCode) {
            case 38: // up arrow
                this.setState({Paddle1Height: this.state.Paddle1Height - 10});
                break;
            case 40: // down arrow
                this.setState({Paddle1Height: this.state.Paddle1Height + 10});
                break;
            default:
                break;
    };

    };
    // on mouse move update the state
    render() {
        return(
            <div className="FullScreen">

                <div className="Board">
                    < Ball/>
                    < Paddle Paddle1={Paddle1Height}/>
                    <div className="Paddle Paddle2"></div>

                </div>
                <div className="Social"></div>
            </div>

        )
    }
}
