import React from 'react'; 

import { useRef } from 'react';

export class Paddle extends React.Component {
    state = {
      position: { x: 0, y: 0 }
    };
    constructor(props) {
        super(props);
        this.parentRef = useRef(null);
        this.parent = this.parentRef.current;
        this.height = this.parent.offsetHeight;
    }
    componentDidMount() {
     const canvasHeight = this.canvasRef.current.offsetHeight
      window.addEventListener('keydown', this.handleKeyDown);
    }

    componentWillUnmount() {
      window.removeEventListener('keydown', this.handleKeyDown);
    }

    handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          if (this.state.position.y <= 0) return;
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y - 10 }
          }));
          break;
        case 'ArrowDown':
            if (this.state.position.y >= this.height) return;
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y + 10 }
          }));
          break;
        default:
          break;
      }
    };

    render() {
      const { x, y } = this.state.position;

      return (

        <div
          className="Paddle Paddle1"

          style={{
            top: `${y}px`
          }}
        />
      );
    }
  }
