import ReactDOM from 'react-dom/client'; /* ReactDOM is for web */
import './index.css';


let timer = 0;

function Square(props) {
	return (
	  <button className="square" onClick={props.onClick}>
		{props.value}
	  </button>
	)
}

class Game extends React.Component {
	  render() {
		return(
      <div className="game">
        <div className="game-board">
          <h1>Salut</h1>
        < span/>

        </div>
      </div>    
    )
	  }
}  


