/************************************************************************
 * Project: PONG
 * 
 * TODO LIST:
 * 1. Create a canvas : Ball, Paddle, Score 
 * 2. Implement the game logic : status, position, score 
 * 3. Implement the game loop : events = mouse move
 * 4
 ************************************************************************ */


import React from 'react'; /* React is JS lib buildon UI interactive apps Web/Mobile*/
import ReactDOM from 'react-dom/client'; /* ReactDOM is for web */
import './index.css';


let timer = 0;

function numberFormat(number) {
  return number < 10 ? `01:0${number}` : number;
}

function Timer(props) {
	return (
	  <h1>Game : <span>{numberFormat(timer)}</span></h1>
	)
}

class Game extends React.Component {
	  render() {
		return(
      <div className="game">
        <div className="game-board">
          <h1>Salut</h1>
        < Timer/>

        </div>
      </div>    
    )
	  }
}  


// ============================================================================//

// create a root element
// React Dom is for web it will render the game component in the root element
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);

window.setInterval(() => {
    timer++;
    root.render(<Game />, document.getElementById("root"));
}, 1000);
