/************************************************************************
 * Project: PONG
 * 
 * TODO LIST:
 * 1. Create a canvas : Ball, Paddle, Score 
 * 2. Implement the game logic : status, position, score 
 * 3. Implement the game loop : events = mouse move
 * 4
 ************************************************************************ */


import React from 'react'; /* React is JS lib buildon UI interactive apps Web/Mobile*/
import ReactDOM from 'react-dom/client'; /* ReactDOM is for web */
import './index.css';


function numberFormat({number}) {
  return number < 10 ? `00:0${number}` : number;
}

class Timer extends  React.Component {
  constructor(props) {
    super(props)
    this.state = {timer: 0}
  }
  // this function will be called every second
  tick() {
    this.setState({timer: this.timer - 1})
  }
  // this function will be called when the component is mounted
  componentDidMount() { 
    this.interval = setInterval(() => this.tick(), 1000)
  }

  render() 
  {
	  return (
      this.componentDidMount(),
	  <h1 id ="Timer" >Game : <span>{numberFormat(this.state.timer)} .</span></h1>
	)}
}

class Game extends React.Component {

    constructor(props) {
        super(props);

    }

	  render() {
		return(
      <div className="game">
        <div className="game-board">
          <h1>Salut</h1>
        < Timer/>
        </div>
      </div>    
    )
	  }
}  


// ============================================================================//

// create a root element
// React Dom is for web it will render the game component in the root element
const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);

window.setInterval(() => {

    root.render(<Game />, document.getElementById("root"));
}, 1000);
