import React from 'react'; 

export class MovePaddle extends React.Component {
    state = {
      position: { x: 0, y: 0 }
    };

    componentDidMount() {
      window.addEventListener('keydown', this.handleKeyDown);
    }

    componentWillUnmount() {
      window.removeEventListener('keydown', this.handleKeyDown);
    }

    handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y - 10 }
          }));
          break;
        case 'ArrowDown':
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y + 10 }
          }));
          break;
        default:
          break;
      }
    };

    render() {
      const { x, y } = this.state.position;

      return (
        <div
          className="Paddle1"
          style={{
            left: `${x}px`,
            top: `${y}px`
          }}
        />
      );
    }
  }
