import React from 'react'; 

import {Ball} from './Ball.jsx';
import {Paddle} from './Paddles.jsx'
import { useState, useEffect, useRef } from "react";

MovePaddle = (NewHeight) => {
    const parentRef = useRef(null);
    this.setState({ Paddle1Height: NewHeight });

    useEffect(() => {
        const parent = parentRef.current;
        const height = parent.offsetHeight;
        const handleKeyDown = event => {
            switch (event.keyCode) {
                case 38: // up arrow
                    if (position > 0)
                      NewHeight(Paddle1Height => position - 10);
                    break;
                case 40: // down arrow
                    if (position + 100 < height)
                     NewHeight(Paddle1Height => position + 10);
                    break;
                default:
                    break;
            }
        };
        document.addEventListener("keydown", handleKeyDown);
        return () => {
            document.removeEventListener("keydown", handleKeyDown);
        };
    }, []);
};

// rerennder the board component when the state changes
export class   Board extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            ballX: 0,
            ballY: 0,
            ballSpeedX: 1,
            ballSpeedY: 0,
            Paddle1Height: 0,
            Paddle2Height: 0,
            player1Score: 0,
            player2Score: 0,
            time: 0,
            MousePosition: { x: 0, y: 0 },
        };
        this.Paddle1Height = this.UpdatePaddle1Height.bind(this);
        this.UpdatePaddle1Height = this.UpdatePaddle1Height.bind(this);
    }

    UpdatePaddle1Height(NewHeight) {
        this.setState({ Paddle1Height: NewHeight });
    };


    // on mouse move update the state
    render() {
        return(
            <div className="FullScreen">

                <div className="Board">
                    < Ball/>
                    < Paddle Paddle1={this.Paddle1Height}/>
                    <div className="Paddle Paddle2"></div>

                </div>
                <div className="Social"></div>
            </div>

        )
    }
}
