import React from 'react'; 

import { useRef } from 'react';

export const Paddle = () => {
     state = {
     position: { x: 0, y: 0 }
    };


    handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          if (this.state.position.y <= 0) return;
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y - 10 }
          }));
          break;
        case 'ArrowDown':
            if (this.state.position.y >= 480) return;
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y + 10 }
          }));
          break;
        default:
          break;
      }
    };

      const { x, y } = this.state.position;

      return (
        <div
          className="Paddle Paddle1"
          style={{
            top: `${y}px`
          }}
        />
        );
  }
