import React from 'react'; 

import { useState, useEffect, useRef } from "react";

export const Paddle = () => {
    const parentRef = useRef(null);
    const parent = parentRef.current;
    const height = parent.offsetHeight;

    const [Position, handleKey] = useState(parent.offsetHeight / 2);

    handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          if (this.state.position.y <= 0) return;
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y - 10 }
          }));
          break;
        case 'ArrowDown':
            if (this.state.position.y >= 1000) return;
          this.setState(prevState => ({
            position: { ...prevState.position, y: prevState.position.y + 10 }
          }));
          break;
        default:
          break;
