import React from 'react'; 

import { useState, useEffect, useRef } from "react";


export class PaddleMove extends React.Component {
  componentDidMount() {
    document.addEventListener('keydown', this.handleKeyPress);
  }

  componentWillUnmount() {
    document.removeEventListener('keydown', this.handleKeyPress);
  }

  handleKeyPress = event => {
    if (event.key === 'ArrowUp') {
      this.props.setValue(this.props.value + 1);
    }
    if (event.key === 'ArrowDown') {
      this.props.setValue(this.props.value - 1);
    }
  };

  render() {
    return null;
  }
}

export const Paddle = (props) => {
    const parentRef = useRef(null);
    const [position, setPosition] = useState(0);
    // const parent = parentRef.current;
    // const height = parent.offsetHeight;

    // const [Position, handleKey] = useState(parent.offsetHeight / 2);

    useEffect(() => {
      const parent = parentRef.current;
      const height = parent.offsetHeight;

      const handleKeyDown = event => {
        switch (event.keyCode) {
          case 38: // up arrow
          setPosition(position => position - 10);

            break;
          case 40: // down arrow
          if (position + 100 < height)
             setPosition(position => position + 10);
            break;
          default:
            break;
        }
      };

      document.addEventListener("keydown", handleKeyDown);
      return () => {
        document.removeEventListener("keydown", handleKeyDown);
      };
    }, []);


    return (
      <div
      ref={parentRef}
      className="Board"
      style={{
        width: '100%',
        height: '100%'
      }}
      >
        <div
          className="Paddle Paddle1"
          style={{
            top: `${position}px`
          }}
        />
      <h1>{position}</h1>
      </div>
      );
    };
