import React from 'react'; /* React is JS lib buildon UI interactive apps Web/Mobile*/
import ReactDOM from 'react-dom/client'; /* ReactDOM is for web */
import './index.css';


import { Board } from './Board.jsx';
// import { Paddles } from './Paddles.jsx';



// ============================================================================//
// Pong Game  : Main component 
// ============================================================================//
const Pong = () => {
  const [ballPosition, setBallPosition] = useState({ x: 50, y: 50 });
  const [ballDirection, setBallDirection] = useState({ x: 5, y: 5 });
  const canvasRef = useRef(null);

  const moveBall = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillRect(ballPosition.x, ballPosition.y, 10, 10);

    setBallPosition({
      x: ballPosition.x + ballDirection.x,
      y: ballPosition.y + ballDirection.y
    });

    if (ballPosition.x + 10 >= canvas.width || ballPosition.x <= 0) {
      setBallDirection({ x: -ballDirection.x, y: ballDirection.y });
    }

    if (ballPosition.y + 10 >= canvas.height || ballPosition.y <= 0) {
      setBallDirection({ x: ballDirection.x, y: -ballDirection.y });
    }
  };

  return (
    <canvas
      ref={canvasRef}
      width={500}
      height={500}
      onClick={moveBall}
    />
  );
};

