/************************************************************************
 * Project: PONG
 * 
 * TODO LIST:
 * 1. Create a canvas : Ball, Paddle, Score 
 * 2. Implement the game logic : status, position, score 
 * 3. Implement the game loop : events = mouse move
 * 4
 ************************************************************************ */


import React from 'react'; /* React is JS lib buildon UI interactive apps Web/Mobile*/
import ReactDOM from 'react-dom/client'; /* ReactDOM is for web */
import './index.css';


function numberFormat({number}) {
  return number < 10 ? `00:0${number}` : number;
}

// ============================================================================//
// Timer component
// ============================================================================//
class Timer extends  React.Component {
  constructor(props) {
    // call the parent constructor
    super(props)

    this.state = {timer : props.start, step }
    this.time = null
  }

  //  be called when the component is mounted
  componentDidMount() { 
    window.setInterval(this.tick.bind(this), 1000)
  }
  // called when the component is unmounted
  componentWillUnmount(){
    this.cleaInterval(this.time)
  }
  // called every second and increment the timer
  tick() {
    this.setState((state, props) => ({timer: state.timer + state.step}))
  }

  stop() {
    this.state.step = 0
  }

  render() 
  {
	  return (

	  <div >Game : {this.state.timer} 
    <button onClick={this.stop.bind(this)}>Stop</button>
    </div> 
	)}
}
// Default props for the Timer component
Timer.defaultProps = {
  start: 0,
  step: 1
}
// Manual props for the Timer component
class ManualIncementer extends React.Component {
  constructor(props) {
    super(props)
    this.state = {timer: 0}

  }

  // 
  increment(e) {
    console.log(e)
    this.setState({timer: this.state.timer + 1})
  }

  // bind is used to bind the function to the current context
  render(){
    return (
      <div>
        Timer : {this.state.timer} <br></br>
        <button onClick={this.increment.bind(this)}>Increment</button>
      </div>
    )
  }
}



// ============================================================================//
// Game component : Main component 
// ============================================================================//
class Game extends React.Component {

	  render() {
		return(
      <div className="game">
        <div className="game-board">
          <h1>Salut</h1>
         < Timer />
         <ManualIncementer />
        </div>
      </div>    
    )
	  }
}  

// ============================================================================//

// create a root element
// React Dom is for web it will render the game component in the root element
const root = ReactDOM.createRoot(document.getElementById("root"));

window.setInterval(() => {root.render(<Game />);}, 1000);
